import { Component, OnDestroy, OnInit } from '@angular/core';

@Component({
  selector: 'app-about',
  standalone: false,
  
  templateUrl: './about.component.html',
  styleUrl: './about.component.css'
})
export class AboutComponent implements OnInit, OnDestroy {


  ngOnDestroy() {
    console.log("Component Destroyed - ngOnDestroy");
  }


  ngOnInit() {
    console.log("this is working about")
  }

}
<section class="about" [dir]="'dir' | translate">
  <div appScrollAnimation class="about-image">
    <img src="assets/images/zee.jpg" alt="image LCP" loading="lazy" />
  </div>
  <div appScrollAnimation class="about-content">
    <h2 appScrollAnimation class="heading">{{'about.h2' | translate}} <span style="color:orangered">{{'about.h' | translate}}</span></h2>
    <h3 appScrollAnimation>{{'about.h3' | translate}}<span style="color:orangered">{{'about.h1' | translate}}</span></h3>
    <p appScrollAnimation>
      {{'about.p' | translate}}
    </p>
    <a appScrollAnimation [routerLink]="['/experience']" class="btn-2">{{'navbar.experience' | translate}}</a>
  </div>
</section>
