      link:'https://ecommerce.com'
    },
  ]

}
<section>
  <div class="project">
    <h1>My <span style="color:orangered">projects</span></h1>
  </div>
  <div class="cards">
    <div *ngFor="let project of projects" class="card">
      <div class="imgs">
        <img src="{{project.image}}" />
      </div>
      <h3>{{project.name}}</h3>
      <p>{{project.description}}</p>
      <button class="btn">view</button>
    </div>
  </div>
</section>
